installer-gui-script.script:
function checkJRE() {
var r = system.files.fileExistsAtPath('/Library/Internet Plug-Ins/JavaAppletPlugin.plugin/Contents/Home');
if (r) return true;
my.result.type = 'Fatal';
my.result.title = "Oracle's Java is needed";
my.result.message = "Oracle's Java For Mac OS X is needed; please install Java 7 or later in advance from http://www.java.com/.";
return false;
}
